function scrollFunction() {
    let e = document.getElementById("image-brand"), n = document.getElementById("main");
    var o = "expanded";
    50 < document.body.scrollTop || 50 < document.documentElement.scrollTop ? (e.classList.remove(o), 
    n.classList.remove(o)) : (e.classList.add(o), n.classList.add(o));
}

window.onscroll = function() {
    scrollFunction();
};